mapper.select:
		select pk_id as id, gmt_create as createTime, gmt_modified as modifiedTime,
        title, content,
        is_anonymous as isAnonymous, user_id as userId,answerCount
        from question left outer join  ((select question_id as pk_id, count(*) as answerCount from answer group by question_id) as A) using(pk_id)
		where pk_id = #{id}

		select pk_id as id, gmt_create as createTime, gmt_modified as modifiedTime,
        title, content,
        is_anonymous as isAnonymous, user_id as userId,answerCount
        from question left outer join  ((select question_id as pk_id, count(*) as answerCount from answer group by question_id) as A) using(pk_id)
		where user_id = #{userId}
